import React, {Component} from 'react';
import './App.css';


//Mỗi lần thay đổi ô input(onHandleChange) thì lưu lại giá trị o input vào state
//Submit thì gửi state đi thôi 
class App extends Component{
  constructor(props)
  {
    super(props);
    this.state={
      txtUserName:"Cuong",//phải trùng với name ô input
      txtPassword:"123",
      txtDesc:"đậu xanh",
      sltGender:1,// Gán luôn giá trị mậc định cho select luôn
      rdLang:'vi',
      chkbStatus:true,// mặc định ban đầu là k check
      
      //Nếu muốn cho giá trị mặc định và đổ luôn ra ngoài input thì phải thêm value vào input
      //Nếu k nó sẽ k đổ ra được giá trị mặc định này ra ngoài input
      // và đi chung với value luôn phải có onChange
    };
  }

  onHandleChange=(event)=>{
    var target= event.target;//target ở đây là ô input
    var name =target.name;// lấy name từng input
    // var value= target.value;
    var value= target.type==='checkbox'? target.checked: target.value;// vì có checkbox nó là kiểu bool phải v
    
    this.setState({
      [name]:value// set kiểu dynamic 
                  //thay vì txtUsername:value ; hay txtPassWord:value
                  //thì ta lấy [name]:value do name = txtUserName.... tuỳ từng input name sẽ khác nhau
                  //thì cái key dc set sẽ khác nhau
    })
  }
  onSubmitForm=(event)=>{
    event.preventDefault();
    console.log(this.state)
  }
  render(){
    return(
      <div className="container mt-30">
        <div className="row">
          <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">
              <div className="panel panel-info">
                  <div className="panel-heading">
                    <h3 className="panel-title">Lesson08-Form</h3>
                  </div>
                  <div className="panel-body">
                    <form  onSubmit={this.onSubmitForm}>
                      {/* Type Text */}
                      <div className="form-group">
                        <label >User Name</label>
                        <input  type="text" 
                                className="form-control" 
                                placeholder="Input field"
                                name="txtUserName"
                                onChange={this.onHandleChange}
                                required="required"
                                value={this.state.txtUserName}
                        />
                      </div>
                      {/* Type password */}
                      <div className="form-group">
                        <label >Password</label>
                        <input  type="password" 
                                className="form-control" 
                                placeholder="Input field"
                                name="txtPassword"
                                onChange={this.onHandleChange}
                                required="required"
                                value={this.state.txtPassword}
                        />
                      </div>
                      {/* Type text area */}
                      <div className="form-group">
                        <label >Text Area</label>                    
                        <textarea 
                            name="txtDesc"  
                            className="form-control" 
                            rows="5" 
                            required="required"
                            onChange={this.onHandleChange} 
                            value={this.state.txtDesc}
                        /> 
                      </div>
                      {/* Type select */}
                      <div className="form-group">
                        <label >Giới tính</label>
                        <select 
                            name="sltGender" 
                            className="form-control" 
                            required="required"
                            value={this.state.sltGender}
                            onChange={this.onHandleChange}
                        >
                          <option value={0} >Nữ</option>
                          <option value={1}>Nam</option>   
                        </select>
                      </div>
                      {/* Radio button */}
                      <div className="form-group">
                        <label >Language</label>
                        <div className="radio">
                          <label>
                            <input 
                                type="radio" 
                                name="rdLang"// đặt trùng tên => chỉ dc 1 trong 2  
                                value="vi" 
                                checked={this.state.rdLang==="en"}
                                onChange={this.onHandleChange}
                            />
                            Tiếng Anh
                          </label>
                          <br/>
                          <label>
                            <input 
                                type="radio" 
                                name="rdLang" 
                                value="en" 
                                checked={this.state.rdLang==="vi"}
                                onChange={this.onHandleChange}
                            />
                            Tiếng Việt
                          </label>
                        </div>
                         {/* Check box */}
                      <div className="form-group">
                        <label >Giới tính</label>
                        <div className="checkbox">
                          <label>
                            <input 
                              type="checkbox" 
                              name="chkbStatus"
                              checked={this.state.chkbStatus===true}
                              // khi giá trị = true thì mới check
                              
                              value={false}
                              onChange={this.onHandleChange}
                            />
                            Checkbox
                          </label>
                        </div>                    
                      </div>
                      </div>
                      <button type="submit" className="btn btn-primary">Submit</button> &nbsp;
                      <button type="reset" className="btn btn-danger">Reset</button>
                    </form>
                  </div>
              </div>
          </div>
        </div>
        
      </div>
    );
  }
} 

export default App;
