import React,{Component} from 'react';


class Sort extends Component{

    render(){
        return( 
            <div className="dropdown">
                <button className="btn btn-info dropbtn">Sort &ensp;
                <i className="fa fa-caret-down"></i>
                </button>
                {/*   input group btn fontsize=0   ne6n phai chinh lai */}
                <ul class="dropdown-menu">
        <li><a href="#">Submenu 1-1</a></li>
        <li><a href="#">Submenu 1-2</a></li>
        <li><a href="#">Submenu 1-3</a></li>                        
      </ul>
                <div className="dropdown-content">
                <li><i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên A-Z</li>
                <li><i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên Z-A</li>
                <br></br>
                {/*<hr style={{width:"80%", height:"1px"}}></hr> */}
                <li>Trạng Thái Kích Hoạt</li>
                <li>Trạng Thái Ẩn</li>
                </div>
            </div> 
        )
    }

}
export default Sort
