import React,{Component} from 'react';


class Sort extends Component{
    constructor(props){
        super(props);
    }

    render(){
        return( 
            <div className="dropdown">
                <button className="btn btn-info dropbtn dropdown-toggle " data-toggle="dropdown">Sort &ensp;
                <i className="fa fa-caret-down"></i>
                </button>
                {/*   input group btn fontsize=0   ne6n phai chinh lai */}
                <ul 
                    class="dropdown-content" 
                    style={{listStyle:"none", paddingLeft:0}}
                >
                    {/* sử dụng arrow function mới truyền dc tham số*/}
                    <li className="seleted" onClick={()=>this.onClick("name",1)}>{/*sort theo name , từ a-z*/}
                        <a role="button" class="sort-selected">
                            <i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên A-Z
                        </a>
                    </li>
                    <li className="seleted" onClick={()=>this.onClick("name",-1)}>{/*sort theo name , từ z-a*/}
                        <a role="button">
                            <i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên Z-A
                        </a>
                    </li>
                    <br></br>
                    {/*<hr style={{width:"80%", height:"1px"}}></hr> */}
                    <li className="seleted" onClick={()=>this.onClick("name",-1)}>{/*sort theo kích hoat , từ z-a*/}
                        <a role="button">Trạng Thái Kích Hoạt</a>
                    </li>
                    <li className="seleted" onClick={()=>this.onClick("name",-1)}>{/*sort theo name , từ z-a*/}
                        <a role="button">Trạng Thái Ẩn</a>
                    </li>                     
                </ul>
            </div> 
        )
    }

}
export default Sort
