import React,{Component} from 'react'


class TaskForm extends Component{
    constructor(props){
        super(props);
        this.state={
            id:"",
            name:"",
            status:false,
        };
    };
   
    componentWillMount(){// chỉ được gọi khi thg prop trước khi prop dc gọi lần 1
        if(this.props.task){// gọi prop lần 2 thì componentWillMount sẽ không dc gọi thêm nữa
            this.setState({//=> khi bấm thêm , mà bấm tiếp sửa => nó vẫn là thêm chứ k phải là sửa nữa
                id:this.props.task.id,
                name:this.props.task.name,
                status:this.props.task.status
            })
        };
    }

    // khi bấm thêm => component TaskForm dc tạo => componentWillMount dc tạo => bấm sửa (hay bấm sửa 2 lần)
    //=> k thể chạy lại componentWillMount 
    //=> nó vẫn giữ nguyên là thêm
    //=> sử dụng componentWillReceiveProps để nếu nhận dc 1 yêu cầu khác
    componentWillReceiveProps(nextProps){
        if(nextProps&&nextProps.task){
            this.setState({
                id:nextProps.task.id,
                name:nextProps.task.name,
                status:nextProps.task.status
            })
        };
        if(nextProps.task===null){// từ sửa sang thêm (lúc này this.props.task = this.props.editing==null)
            console.log("dauxanh")
            // this.setState({
            //     id:"",
            //     name:"",
            //     status:false
            // })
        }
    }
    closeTaskForm=()=>{
        this.props.closeTaskForm();// gọi đến cái prop khai báo ở thg cha
    }
    onChange=(event)=>{
        var target= event.target;//target ở đây là ô input
        var name = target.name;
        var value= target.value;
        this.setState({
            [name]:value// set kiểu dynamic 
                        //thay vì txtUsername:value ; hay txtPassWord:value
                        //thì ta lấy [name]:value do name = txtUserName.... tuỳ từng input name sẽ khác nhau
                        //thì cái key dc set sẽ khác nhau
        })
    }
    onClear=()=>{
        this.setState({
            name:"",
            status:false
        })
    }
    onSubmit=(event)=>{
        // this.setState({
        //     name:"dauxanh",
        //     status:true
        // });
        // state là bất đồng bộ 
        // => thay đổi state 
        //nó vẫn sẽ gửi vẫn là state cũ
        this.props.onSubmit(this.state);
    }
    render(){
        var {id} = this.state
        return( 
            <div class="panel panel-danger">
                <div class="panel-heading" >
                    <strong class="panel-title " >
                      {id!==''?"Cập nhật công việc":"Thêm công việc"}
                      {/* Thêm và sửa khác nhau chỗ có id hay k có id */}
                    </strong> 
                    <span className="fa fa-times-circle fa-pull-right"  aria-hidden="true" onClick={this.closeTaskForm}></span>
                   
                </div>
                <div class="panel-body">
                    <strong>Tên :</strong>
                    <input 
                        type="email" 
                        name="name" 
                        id="input" 
                        class="form-control"  
                        required="required"
                        value={this.state.name}
                        onChange={this.onChange}
                    />
                    <br/>
                    <strong>Trạng Thái</strong>
                    <select 
                        name="status" 
                        id="input" 
                        class="form-control" 
                        required="required"
                        onChange={this.onChange}
                        value={this.state.status}
                    >
                        <option value={false}>Ẩn </option>
                        <option value={true}>Kích Hoạt</option>
                    </select>
                    <br/>
                    <button 
                        type="button" 
                        class="btn btn-success"
                        onClick={this.onSubmit}
                    >{id!=''?"Sửa":"Thêm"}</button>  &nbsp;
                    <button 
                        type="button" 
                        class="btn btn-danger"
                        onClick={this.onClear}
                    >Reset</button>
                </div>
            </div>    
        )
    }

}
export default TaskForm
