import React,{Component} from 'react';


class Sort extends Component{

    render(){
        return( 
            <div className="dropdown">
                <button className="btn btn-info dropbtn dropdown-toggle " data-toggle="dropdown">Sort &ensp;
                <i className="fa fa-caret-down"></i>
                </button>
                {/*   input group btn fontsize=0   ne6n phai chinh lai */}
                <ul class="dropdown-content" style={{listStyle:"none"}}>
                    <li><a style={{paddingTop:10, paddingLeft:0}}><i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên A-Z</a></li>
                    <li><i class="fa fa-sort-alpha-asc" aria-hidden="true"></i>  Tên Z-A</li>
                    <br></br>
                    {/*<hr style={{width:"80%", height:"1px"}}></hr> */}
                    <li>Trạng Thái Kích Hoạt</li>
                    <li>Trạng Thái Ẩn</li>                     
                </ul>
            </div> 
        )
    }

}
export default Sort
