import React ,{Component} from 'react';

class TaskItem extends Component{
  constructor(props){
    super(props);
  }

   onUpdateStatus=()=>{
     this.props.onUpdateStatus(this.props.task.id)
   }
   onDelete=()=>{
     this.props.onDelete(this.props.task.id)
   }
   onEdit=()=>{
     this.props.onEdit(this.props.task.id)
   }
   onShow=()=>{
    console.log(this.props.task.status)
   }
  render(){
    var {task}= this.props
   return( 
    <tr>
        <td>{task.id}</td>
        <td>{task.name}</td>
        <td style={{textAlign:"center"}}><div onClick={this.onUpdateStatus} className={task.status===true?"active":"unactive"}  >{task.status===true?"Kích Hoat":"Ẩn "}</div></td>
        <td>
            <button type="button" class="btn btn-info"onClick={this.onEdit} > 
              <i class="fa fa-fa fa-pencil-square-o" aria-hidden="true" ></i> Sửa
            </button> &#160;
            <button type="button" class="btn btn-danger" onClick={this.onDelete}>
            <i class="fa fa-trash-o" aria-hidden="true"></i> Xoá
            </button>   
        </td>
    </tr>
   );
      
  }
}

export default TaskItem;
